// Place your key bindings in this file to override the defaults
[
    // keybindings for R scripts. 
    {
        "key": "Ctrl+Shift+m",
        "command": "type",
        "args": { "text": " %>% " },
        "when": "editorTextFocus && editorLangId == r"
      },
      {
        "key": "Alt+-",
        "command": "type",
        "args": { "text": " <- " },
        "when": "editorTextFocus && editorLangId == r"
      },
      // keybindings for Rmarkdown
      {
        "key": "Ctrl+Shift+m",
        "command": "type",
        "args": { "text": " %>% " },
        "when": "editorTextFocus && editorLangId == rmd"
      },
      {
        "key": "Alt+-",
        "command": "type",
        "args": { "text": " <- " },
        "when": "editorTextFocus && editorLangId == rmd"
      },
      // keybindings for R terminal (radian included)
      {
        "key": "Ctrl+Shift+m",
        "command": "workbench.action.terminal.sendSequence",
        "args": { "text": " %>% " },
        "when": "terminalFocus"
      },
      {
        "key": "Alt+-",
        "command": "workbench.action.terminal.sendSequence",
        "args": { "text": " <- " },
        "when": "terminalFocus"
      },
      // Insert R Code chunk
      {
        "key": "ctrl+alt+i". 
        "command": "editor.action.insertSnippet",
        "when": "editorTextFocus",
        "args": {"snippet": "```{r}\n$0\n```"}
      },
      {
        "key": "ctrl+alt+o". 
        "command": "editor.action.insertSnippet",
        "when": "editorTextFocus",
        "args": {"snippet": "options(\n  max.print=100,\n  vsc.use_httpgd=TRUE,\n  device='quartz'\n)"}
      },
      {
        "key": "ctrl+alt+m",
        "command": "editor.action.insertSnippet",
        "when": "editorTextFocus",
        "args":{
          "snippet": "---\ntitle: '$0'\nauthor: 'ì´ê´‘ì¶˜'\ndate: '2023-01-31'\noutput:\n  pagedown::html_paged:\n    self_contained: true\n    toc: false\n---\n\n```{r setup, include=FALSE}\nknitr::opts_chunk\\$set(\n  echo = FALSE,\n  message = FALSE,\n  warning=FALSE\n)\n```"
        }
      },

